const finalize = (data: { q: string, o: string[], a: string, e: string }[], mod: number, cat: string): QuizQuestion[] => 
  data.map((item, idx) => ({
    id: `${mod}.${idx + 1}`,
    category: cat,
    directSpeech: item.q.includes(': "') ? item.q.split(': "')[1].slice(0, -1) : item.q,
    context: `Conceptual Mastery Level ${mod}`,
    options: item.o,
    correctAnswer: item.a,
    explanation: item.e,
    errorType: (mod === 5 || mod === 6 ? 'time_place' : mod === 4 ? 'reporting_verb' : 'tense') as ErrorType
  }));

